  --shadow-xl: 0px 20px 40px 0px hsl(220 15% 12% / 0.12), 0px 10px 20px -5px hsl(220 15% 12% / 0.10);
  --shadow-2xl: 0px 30px 60px 0px hsl(220 15% 12% / 0.15);
