  --input: 220 15% 28%;
  --ring: 250 70% 60%;
  --chart-1: 250 70% 65%;
  --chart-2: 142 70% 55%;
  --chart-3: 38 90% 60%;
  --chart-4: 200 80% 60%;
  --chart-5: 280 65% 65%;

  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.20);
  --shadow-xs: 0px 1px 3px 0px hsl(0 0% 0% / 0.25);
  --shadow-sm: 0px 2px 4px 0px hsl(0 0% 0% / 0.22), 0px 1px 2px -1px hsl(0 0% 0% / 0.18);
  --shadow: 0px 4px 6px 0px hsl(0 0% 0% / 0.25), 0px 2px 4px -1px hsl(0 0% 0% / 0.20);
  --shadow-md: 0px 6px 12px 0px hsl(0 0% 0% / 0.28), 0px 3px 6px -2px hsl(0 0% 0% / 0.22);
  --shadow-lg: 0px 10px 20px 0px hsl(0 0% 0% / 0.32), 0px 5px 10px -3px hsl(0 0% 0% / 0.25);
