import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0" +
  " hover-elevate active-elevate-2",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground border border-primary-border",
        destructive:
          "bg-destructive text-destructive-foreground border border-destructive-border",
        outline:
          // Shows the background color of whatever card / sidebar / accent background it is inside of.
          // Inherits the current text color.
          " border [border-color:var(--button-outline)]  shadow-xs active:shadow-none ",
        secondary: "border bg-secondary text-secondary-foreground border border-secondary-border ",
        // Add a transparent border so that when someone toggles a border on later, it doesn't shift layout/size.
        ghost: "border border-transparent",
      },
      // Heights are set as "min" heights, because sometimes Ai will place large amount of content
      // inside buttons. With a min-height they will look appropriate with small amounts of content,
      // but will expand to fit large amounts of content.
      size: {
        default: "min-h-9 px-4 py-2",
        sm: "min-h-8 rounded-md px-3 text-xs",
        lg: "min-h-10 rounded-md px-8",
        icon: "h-9 w-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }

shared/schema.ts.content:
import { z } from "zod";

// User Schema
export const userSchema = z.object({
  _id: z.string(),
  username: z.string().min(3).max(30),
  password: z.string().min(6),
  isAdmin: z.boolean().default(false),
  createdAt: z.date().or(z.string()),
});

export const insertUserSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters").max(30),
  password: z.string().min(6, "Password must be at least 6 characters"),
  isAdmin: z.boolean().optional().default(false),
});

export const loginSchema = z.object({
  username: z.string().min(1, "Username is required"),
  password: z.string().min(1, "Password is required"),
});

export type User = z.infer<typeof userSchema>;
export type InsertUser = z.infer<typeof insertUserSchema>;
export type LoginCredentials = z.infer<typeof loginSchema>;

// Room Schema
export const roomModeSchema = z.enum(["screenshare", "watchparty"]);

export const participantSchema = z.object({
  userId: z.string(),
  username: z.string(),
  joinedAt: z.date().or(z.string()),
});

export const roomSchema = z.object({
  _id: z.string(),
  name: z.string(),
  ownerId: z.string(),
  ownerUsername: z.string(),
  mode: roomModeSchema,
  videoUrl: z.string().optional(),
  participants: z.array(participantSchema),
  isActive: z.boolean(),
  createdAt: z.date().or(z.string()),
  updatedAt: z.date().or(z.string()),
});

export const insertRoomSchema = z.object({
  name: z.string().min(1, "Room name is required").max(50),
  mode: roomModeSchema.optional().default("watchparty"),
  videoUrl: z.string().url().optional(),
});

export const updateRoomSchema = z.object({
  name: z.string().min(1).max(50).optional(),
  mode: roomModeSchema.optional(),
  videoUrl: z.string().url().optional().or(z.literal("")),
  ownerId: z.string().optional(),
});

export type Room = z.infer<typeof roomSchema>;
export type InsertRoom = z.infer<typeof insertRoomSchema>;
export type UpdateRoom = z.infer<typeof updateRoomSchema>;
export type RoomMode = z.infer<typeof roomModeSchema>;
export type Participant = z.infer<typeof participantSchema>;

// Message Schema
export const messageSchema = z.object({
  _id: z.string(),
  roomId: z.string(),
  userId: z.string(),
  username: z.string(),
  content: z.string(),
  type: z.enum(["text", "gif", "system"]),
  gifUrl: z.string().optional(),
  createdAt: z.date().or(z.string()),
});

export const insertMessageSchema = z.object({
  roomId: z.string(),
  content: z.string().min(1).max(1000),
  type: z.enum(["text", "gif", "system"]).optional().default("text"),
  gifUrl: z.string().url().optional(),
});

export type Message = z.infer<typeof messageSchema>;
export type InsertMessage = z.infer<typeof insertMessageSchema>;

// WebSocket Event Types
export const wsEventSchema = z.object({
  type: z.enum([
    "message",
    "user_joined",
    "user_left",
    "mode_changed",
    "video_sync",
    "ownership_transferred",
    "room_updated",
  ]),
  data: z.any(),
  roomId: z.string().optional(),
  userId: z.string().optional(),
  timestamp: z.date().or(z.string()).optional(),
});

export type WSEvent = z.infer<typeof wsEventSchema>;

// Video Sync Event
export const videoSyncSchema = z.object({
  action: z.enum(["play", "pause", "seek"]),
  currentTime: z.number(),
  videoUrl: z.string().optional(),
});

export type VideoSync = z.infer<typeof videoSyncSchema>;

// Auth Response
export const authResponseSchema = z.object({
  token: z.string(),
  user: z.object({
    _id: z.string(),
    username: z.string(),
    isAdmin: z.boolean(),
  }),
});

export type AuthResponse = z.infer<typeof authResponseSchema>;

