import type { Message } from "@/lib/schema";

interface SystemMessageProps {
  message: Message;
}

export function SystemMessage({ message }: SystemMessageProps) {
  const timestamp = new Date(message.createdAt).toLocaleTimeString([], {
    hour: "2-digit",
    minute: "2-digit",
  });

  return (
    <div
      className="flex items-center justify-center px-4 py-2"
      data-testid={`system-message-${message._id}`}
    >
      <div className="flex items-center gap-2 text-xs text-muted-foreground">
        <span data-testid={`text-system-content-${message._id}`}>
          {message.content}
        </span>
        <span className="text-[10px]"></span>
        <span data-testid={`text-system-time-${message._id}`}>{timestamp}</span>
      </div>
    </div>
  );
}

src/pages/room.tsx.content:
import { useEffect, useState } from "react";
import { useLocation, useParams } from "wouter";
import { useAuth } from "@/contexts/AuthContext";
import { useRoom, useJoinRoom, useLeaveRoom, useUpdateRoom, useTransferOwnership } from "@/hooks/useRooms";
import { useMessages, useSendMessage } from "@/hooks/useMessages";
import { useWebSocket } from "@/hooks/useWebSocket";
import { useIsMobile } from "@/hooks/use-mobile";
import { RoomVideo } from "@/components/room-video";
import { RoomControls } from "@/components/room-controls";
import { OwnershipTransfer } from "@/components/ownership-transfer";
import { ChatPanel } from "@/components/chat-panel";
import { BottomNav, type BottomNavTab } from "@/components/bottom-nav";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Skeleton } from "@/components/ui/skeleton";
import { Sheet, SheetContent, SheetHeader, SheetTitle, SheetTrigger } from "@/components/ui/sheet";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { Crown, Users } from "lucide-react";
import type { Room as RoomType, Message, VideoSync } from "@/lib/schema";

export default function Room() {
  const params = useParams<{ id: string }>();
  const [, setLocation] = useLocation();
  const { user, isAuthenticated, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const isMobile = useIsMobile();

  const { data: room, isLoading: roomLoading } = useRoom(params.id);
  const { data: messages = [], isLoading: messagesLoading } = useMessages(params.id);
  const roomData = room as RoomType | undefined;
  const messagesList = (messages || []) as Message[];
  const joinRoomMutation = useJoinRoom(params.id!);
  const leaveRoomMutation = useLeaveRoom(params.id!);
  const updateRoomMutation = useUpdateRoom(params.id!);
  const transferOwnershipMutation = useTransferOwnership(params.id!);
  const sendMessageMutation = useSendMessage(params.id!);

  const [videoSyncEvent, setVideoSyncEvent] = useState<VideoSync | null>(null);
  const [activeTab, setActiveTab] = useState<BottomNavTab>("chat");

  const { sendMessage } = useWebSocket(params.id, {
    onVideoSync: (data: VideoSync) => {
      setVideoSyncEvent(data);
    },
  });

  useEffect(() => {
    if (!authLoading && !isAuthenticated) {
      setLocation("/login");
      return;
    }

    if (roomData && user) {
      const isParticipant = roomData.participants.some((p: any) => p.userId === user._id);
      if (!isParticipant) {
        joinRoomMutation.mutate();
      }
    }
  }, [isAuthenticated, authLoading, roomData, user, setLocation]);

  if (authLoading || !isAuthenticated) {
    return null;
  }

  const isOwner = !!(roomData && user && roomData.ownerId.toString() === user._id);

  const handleModeChange = async (newMode: RoomType["mode"]) => {
    try {
      await updateRoomMutation.mutateAsync({ mode: newMode });
      toast({
        title: "Mode updated",
        description: `Room mode changed to ${newMode === "screenshare" ? "Screen Share" : "Watch Party"}`,
      });
    } catch (error: any) {
      toast({
        title: "Failed to update mode",
        description: error.message || "Something went wrong",
        variant: "destructive",
      });
    }
  };

  const handleLeaveRoom = async () => {
    try {
      await leaveRoomMutation.mutateAsync();
      setLocation("/rooms");
    } catch (error: any) {
      toast({
        title: "Failed to leave room",
        description: error.message || "Something went wrong",
        variant: "destructive",
      });
    }
  };

  const handleOwnershipTransfer = async (newOwnerId: string) => {
    try {
      await transferOwnershipMutation.mutateAsync(newOwnerId);
      toast({
        title: "Ownership transferred",
        description: "Room ownership has been transferred successfully",
      });
    } catch (error: any) {
      toast({
        title: "Failed to transfer ownership",
        description: error.message || "Something went wrong",
        variant: "destructive",
      });
    }
  };

  const handleSendMessage = async (content: string) => {
    try {
      await sendMessageMutation.mutateAsync({ content, type: "text" });
    } catch (error: any) {
      toast({
        title: "Failed to send message",
        description: error.message || "Something went wrong",
        variant: "destructive",
      });
    }
  };

  const handleVideoSync = (syncData: VideoSync) => {
    sendMessage({
      type: 'video_sync',
      data: syncData,
    });
  };

  if (roomLoading || messagesLoading) {
    return (
      <div className="h-full flex flex-col">
        <div className="p-4 border-b">
          <Skeleton className="h-8 w-48" />
        </div>
        <div className="flex-1 flex">
          <div className="flex-1 p-6">
            <Skeleton className="w-full aspect-video" />
          </div>
          <div className="w-80 border-l">
            <Skeleton className="h-full" />
          </div>
        </div>
      </div>
    );
  }

  if (!roomData) {
    return (
      <div className="h-full flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-semibold">Room not found</h2>
          <p className="text-muted-foreground mt-2">The room you're looking for doesn't exist</p>
        </div>
      </div>
    );
  }

  // Desktop Layout: Side-by-side (video left, chat right)
  if (!isMobile) {
    return (
      <div className="h-full flex flex-col" data-testid="room-page">
        {/* Header with room info and controls */}
        <div className="flex-shrink-0 border-b bg-background p-4">
          <div className="flex items-center justify-between gap-4">
            <div className="flex items-center gap-3 min-w-0 flex-1">
              <h2 className="font-semibold truncate text-lg">{roomData.name}</h2>
              <Badge variant="secondary">
                {roomData.mode === "screenshare" ? "Screen Share" : "Watch Party"}
              </Badge>
            </div>

            <div className="flex items-center gap-2 flex-shrink-0">
              {/* Participants Sheet */}
              <Sheet>
                <SheetTrigger asChild>
                  <Button variant="outline" size="sm" className="gap-2">
                    <Users className="h-4 w-4" />
                    Participants ({roomData.participants.length})
                  </Button>
                </SheetTrigger>
                <SheetContent side="right" className="w-96">
                  <SheetHeader>
                    <SheetTitle className="flex items-center justify-between">
                      <span>Participants ({roomData.participants.length})</span>
                      {isOwner && (
                        <OwnershipTransfer
                          participants={roomData.participants}
                          currentOwnerId={roomData.ownerId.toString()}
                          onTransfer={handleOwnershipTransfer}
                        />
                      )}
                    </SheetTitle>
                  </SheetHeader>
                  <ScrollArea className="h-full mt-4">
                    <div className="space-y-2">
                      {roomData.participants.map((participant: any) => (
                        <div
                          key={participant.userId}
                          className="flex items-center gap-3 rounded-md border p-3"
                          data-testid={`participant-${participant.userId}`}
                        >
                          <Avatar className="h-8 w-8">
                            <AvatarFallback className="text-xs">
                              {participant.username.slice(0, 2).toUpperCase()}
                            </AvatarFallback>
                          </Avatar>
                          <span className="text-sm font-medium flex-1">
                            {participant.username}
                          </span>
                          {participant.userId === roomData.ownerId.toString() && (
                            <Badge
                              variant="secondary"
                              className="h-6 px-2"
                              data-testid={`owner-badge-${participant.userId}`}
                            >
                              <Crown className="h-3 w-3 text-primary mr-1" />
                              Owner
                            </Badge>
                          )}
                        </div>
                      ))}
                    </div>
                  </ScrollArea>
                </SheetContent>
              </Sheet>

              {/* Room Controls - For Owner */}
              {isOwner && (
                <RoomControls
                  roomId={roomData._id}
                  roomName={roomData.name}
                  mode={roomData.mode}
                  isOwner={isOwner}
                  onModeChange={handleModeChange}
                  onLeave={handleLeaveRoom}
                />
              )}

              {/* Leave Button - For Non-Owners */}
              {!isOwner && (
                <Button 
                  variant="destructive" 
                  size="sm" 
                  onClick={handleLeaveRoom}
                >
                  Leave Room
                </Button>
              )}
            </div>
          </div>
        </div>

        {/* Main content: Video (left) and Chat (right) */}
        <div className="flex-1 flex min-h-0">
          {/* Video Section */}
          <div className="flex-1 bg-black flex items-center justify-center">
            <div className="w-full h-full">
              <RoomVideo 
                mode={roomData.mode} 
                videoUrl={roomData.videoUrl}
                isOwner={isOwner}
                onVideoSync={handleVideoSync}
                videoSyncEvent={videoSyncEvent}
              />
            </div>
          </div>

          {/* Chat Sidebar */}
          <div className="w-96 flex-shrink-0">
            <ChatPanel messages={messagesList} onSendMessage={handleSendMessage} />
          </div>
        </div>
      </div>
    );
  }

  // Mobile Layout: Stacked (video top, content middle, bottom nav)
  return (
    <div className="h-full flex flex-col pb-16" data-testid="room-page">
      {/* Header with room info and controls */}
      <div className="flex-shrink-0 border-b bg-background p-3">
        <div className="flex items-center justify-between gap-2">
          <div className="flex items-center gap-2 min-w-0 flex-1">
            <h2 className="font-semibold truncate text-sm">{roomData.name}</h2>
            <Badge variant="secondary" className="flex-shrink-0 text-xs">
              {roomData.mode === "screenshare" ? "Screen" : "Watch"}
            </Badge>
          </div>

          <div className="flex items-center gap-2 flex-shrink-0">
            {/* Room Settings - For Owner */}
            {isOwner && (
              <RoomControls
                roomId={roomData._id}
                roomName={roomData.name}
                mode={roomData.mode}
                isOwner={isOwner}
                onModeChange={handleModeChange}
                onLeave={handleLeaveRoom}
              />
            )}

            {/* Leave Button - For Non-Owners */}
            {!isOwner && (
              <Button 
                variant="destructive" 
                size="sm" 
                onClick={handleLeaveRoom}
                data-testid="button-leave-room"
              >
                Leave
              </Button>
            )}
          </div>
        </div>
      </div>

      {/* Video Section - Top */}
      <div className="flex-shrink-0 bg-black">
        <div className="aspect-video">
          <RoomVideo 
            mode={roomData.mode} 
            videoUrl={roomData.videoUrl}
            isOwner={isOwner}
            onVideoSync={handleVideoSync}
            videoSyncEvent={videoSyncEvent}
          />
        </div>
      </div>

      {/* Content Section - Middle (flexible height based on active tab) */}
      <div className="flex-1 min-h-0 border-t">
        {activeTab === "chat" && (
          <ChatPanel messages={messagesList} onSendMessage={handleSendMessage} />
        )}

        {activeTab === "participants" && (
          <div className="h-full flex flex-col">
            <div className="flex-shrink-0 p-4 border-b">
              <div className="flex items-center justify-between">
                <h3 className="font-semibold">Participants ({roomData.participants.length})</h3>
                {isOwner && (
                  <OwnershipTransfer
                    participants={roomData.participants}
                    currentOwnerId={roomData.ownerId.toString()}
                    onTransfer={handleOwnershipTransfer}
                  />
                )}
              </div>
            </div>
            <ScrollArea className="flex-1">
              <div className="p-4 space-y-3">
                {roomData.participants.map((participant: any) => (
                  <div
                    key={participant.userId}
                    className="flex items-center gap-3 rounded-md border p-3"
                    data-testid={`participant-${participant.userId}`}
                  >
                    <Avatar className="h-10 w-10">
                      <AvatarFallback className="text-sm">
                        {participant.username.slice(0, 2).toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    <span className="text-sm font-medium flex-1">
                      {participant.username}
                    </span>
                    {participant.userId === roomData.ownerId.toString() && (
                      <Badge
                        variant="secondary"
                        className="h-6 px-2"
                        data-testid={`owner-badge-${participant.userId}`}
                      >
                        <Crown className="h-3 w-3 text-primary mr-1" />
                        Owner
                      </Badge>
                    )}
                  </div>
                ))}
              </div>
            </ScrollArea>
          </div>
        )}
      </div>

      {/* Bottom Navigation */}
      <BottomNav 
        activeTab={activeTab} 
        onTabChange={setActiveTab}
        participantCount={roomData.participants.length}
        roomName={roomData.name}
        roomMode={roomData.mode}
        isOwner={isOwner}
        onModeChange={handleModeChange}
        onLeaveRoom={handleLeaveRoom}
      />
    </div>
  );
}

src/components/app-sidebar.tsx.content:
import { Link, useLocation } from "wouter";
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarFooter,
  SidebarHeader,
} from "@/components/ui/sidebar";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Video,
  Users,
  Shield,
  Crown,
  Circle,
  LogOut,
} from "lucide-react";
import type { Room } from "@/lib/schema";
import { useAuth } from "@/contexts/AuthContext";
import { useRooms } from "@/hooks/useRooms";

export function AppSidebar() {
  const [location, setLocation] = useLocation();
  const { user, isAuthenticated, logout } = useAuth();
  const { data: rooms, isLoading: roomsLoading } = useRooms();

  const isActive = (path: string) => location === path;

  // Filter active rooms with participants
  const activeRooms = ((rooms || []) as Room[]).filter(
    (room) => room.isActive && room.participants.length > 0
  );

  const handleLogout = async () => {
    await logout();
    setLocation("/login");
  };

  // Don't render sidebar if not authenticated
  if (!isAuthenticated) {
    return null;
  }

  return (
    <Sidebar data-testid="app-sidebar">
      <SidebarHeader className="p-4">
        <div className="flex items-center gap-2">
          <div className="flex h-8 w-8 items-center justify-center rounded-md bg-primary">
            <Video className="h-4 w-4 text-primary-foreground" />
          </div>
          <div>
            <h2 className="text-sm font-semibold">WatchTogether</h2>
            <p className="text-xs text-muted-foreground">Watch with friends</p>
          </div>
        </div>
      </SidebarHeader>

      <SidebarContent className="px-2">
        <SidebarGroup>
          <SidebarGroupLabel>Navigation</SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              <SidebarMenuItem>
                <SidebarMenuButton
                  asChild
                  isActive={isActive("/rooms")}
                  data-testid="nav-rooms"
                >
                  <Link href="/rooms">
                    <Users className="h-4 w-4" />
                    <span>All Rooms</span>
                  </Link>
                </SidebarMenuButton>
              </SidebarMenuItem>
              {user?.isAdmin && (
                <SidebarMenuItem>
                  <SidebarMenuButton
                    asChild
                    isActive={isActive("/admin")}
                    data-testid="nav-admin"
                  >
                    <Link href="/admin">
                      <Shield className="h-4 w-4" />
                      <span>Admin Panel</span>
                    </Link>
                  </SidebarMenuButton>
                </SidebarMenuItem>
              )}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>

        <Separator className="my-2" />

        <SidebarGroup>
          <SidebarGroupLabel>Active Rooms</SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              {roomsLoading ? (
                <div className="px-3 py-2 space-y-2">
                  <Skeleton className="h-10 w-full" />
                  <Skeleton className="h-10 w-full" />
                </div>
              ) : activeRooms.length === 0 ? (
                <div className="px-3 py-6 text-center text-sm text-muted-foreground">
                  No active rooms
                </div>
              ) : (
                activeRooms.map((room: Room) => (
                  <SidebarMenuItem key={room._id}>
                    <SidebarMenuButton
                      asChild
                      isActive={isActive(`/room/${room._id}`)}
                      data-testid={`nav-room-${room._id}`}
                    >
                      <Link href={`/room/${room._id}`}>
                        <Circle
                          className={`h-2 w-2 ${
                            room.isActive
                              ? "fill-status-online text-status-online"
                              : "fill-muted text-muted"
                          }`}
                        />
                        <div className="flex-1 min-w-0">
                          <div className="truncate text-sm">{room.name}</div>
                          <div className="flex items-center gap-1 text-xs text-muted-foreground">
                            {user && room.ownerId === user._id && (
                              <Crown className="h-3 w-3" />
                            )}
                            <span className="truncate">
                              {room.participants.length}{" "}
                              {room.participants.length === 1
                                ? "person"
                                : "people"}
                            </span>
                          </div>
                        </div>
                      </Link>
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                ))
              )}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>

      <SidebarFooter className="p-2">
        <SidebarMenu>
          <SidebarMenuItem>
            <SidebarMenuButton asChild className="w-full" data-testid="nav-user-profile">
              <Link href="/profile">
                <Avatar className="h-6 w-6">
                  {user?.avatarUrl && <AvatarImage src={user.avatarUrl} alt={user.username} />}
                  <AvatarFallback className="text-xs">
                    {user?.username.slice(0, 2).toUpperCase() || "??"}
                  </AvatarFallback>
                </Avatar>
                <div className="flex-1 text-left min-w-0">
                  <div className="text-sm font-medium truncate">
                    {user?.username || "Unknown"}
                  </div>
                  {user?.isAdmin && (
                    <Badge variant="secondary" className="h-4 px-1 text-xs">
                      Admin
                    </Badge>
                  )}
                </div>
              </Link>
            </SidebarMenuButton>
          </SidebarMenuItem>
          <SidebarMenuItem>
            <SidebarMenuButton onClick={handleLogout} data-testid="nav-logout">
              <LogOut className="h-4 w-4" />
              <span>Sign Out</span>
            </SidebarMenuButton>
          </SidebarMenuItem>
        </SidebarMenu>
      </SidebarFooter>
    </Sidebar>
  );
}

src/components/chat-message.tsx.content:
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import type { Message } from "@/lib/schema";

interface ChatMessageProps {
  message: Message;
}

export function ChatMessage({ message }: ChatMessageProps) {
  const timestamp = new Date(message.createdAt).toLocaleTimeString([], {
    hour: "2-digit",
    minute: "2-digit",
  });

  return (
    <div
      className="group flex gap-3 px-4 py-2 hover-elevate"
      data-testid={`message-${message._id}`}
    >
      <Avatar className="h-8 w-8 shrink-0">
        <AvatarFallback className="text-xs">
          {message.username.slice(0, 2).toUpperCase()}
        </AvatarFallback>
      </Avatar>
      <div className="flex-1 min-w-0 space-y-1">
        <div className="flex items-baseline gap-2">
          <span
            className="font-medium text-sm"
            data-testid={`text-message-username-${message._id}`}
          >
            {message.username}
          </span>
          <span
            className="text-xs text-muted-foreground opacity-0 group-hover:opacity-100 transition-opacity"
            data-testid={`text-message-time-${message._id}`}
          >
            {timestamp}
          </span>
        </div>
        {message.type === "gif" && message.gifUrl ? (
          <img
            src={message.gifUrl}
            alt="GIF"
            className="max-w-xs rounded-md"
            data-testid={`img-message-gif-${message._id}`}
          />
        ) : (
          <p
            className="text-sm break-words"
            data-testid={`text-message-content-${message._id}`}
          >
            {message.content}
          </p>
        )}
      </div>
    </div>
  );
}

