src/pages/admin-dashboard.tsx.content:
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useLocation } from "wouter";
import { insertUserSchema, type InsertUser, type User } from "@/lib/schema";
import { useAuth } from "@/contexts/AuthContext";
import { useUsers, useCreateUser, useUpdateUser, useDeleteUser } from "@/hooks/useUsers";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Search, UserPlus, Edit, Trash2, Shield, Users } from "lucide-react";

export default function AdminDashboard() {
  const [, setLocation] = useLocation();
  const { user, isAuthenticated, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const [searchQuery, setSearchQuery] = useState("");
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);

  const { data: users = [], isLoading: usersLoading } = useUsers();
  const usersList = (users || []) as User[];
  const createUserMutation = useCreateUser();
  const updateUserMutation = useUpdateUser();
  const deleteUserMutation = useDeleteUser();

  const createForm = useForm<InsertUser>({
    resolver: zodResolver(insertUserSchema),
    defaultValues: {
      username: "",
      password: "",
      isAdmin: false,
    },
  });

  const editForm = useForm<Partial<InsertUser>>({
    defaultValues: {
      username: "",
      password: "",
      isAdmin: false,
    },
  });

  useEffect(() => {
    if (!authLoading) {
      if (!isAuthenticated) {
        setLocation("/login");
        return;
      }
      if (!user?.isAdmin) {
        setLocation("/rooms");
        return;
      }
    }
  }, [isAuthenticated, user, authLoading, setLocation]);

  if (authLoading || !isAuthenticated || !user?.isAdmin) {
    return null;
  }

  const filteredUsers = usersList.filter((u: User) =>
    u.username.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const onCreateUser = async (data: InsertUser) => {
    try {
      await createUserMutation.mutateAsync(data);
      setCreateDialogOpen(false);
      createForm.reset();
      toast({
        title: "User created",
        description: `User "${data.username}" has been created successfully.`,
      });
    } catch (error: any) {
      toast({
        title: "Failed to create user",
        description: error.message || "Something went wrong",
        variant: "destructive",
      });
    }
  };

  const onEditUser = async (data: Partial<InsertUser>) => {
    if (!selectedUser) return;
    try {
      const updateData: any = {};
      if (data.username) updateData.username = data.username;
      if (data.password) updateData.password = data.password;
      if (data.isAdmin !== undefined) updateData.isAdmin = data.isAdmin;

      await updateUserMutation.mutateAsync({
        id: selectedUser._id,
        data: updateData,
      });
      setEditDialogOpen(false);
      setSelectedUser(null);
      editForm.reset();
      toast({
        title: "User updated",
        description: "User has been updated successfully.",
      });
    } catch (error: any) {
      toast({
        title: "Failed to update user",
        description: error.message || "Something went wrong",
        variant: "destructive",
      });
    }
  };

  const onDeleteUser = async () => {
    if (!selectedUser) return;
    try {
      await deleteUserMutation.mutateAsync(selectedUser._id);
      setDeleteDialogOpen(false);
      setSelectedUser(null);
      toast({
        title: "User deleted",
        description: "User has been deleted successfully.",
      });
    } catch (error: any) {
      toast({
        title: "Failed to delete user",
        description: error.message || "Something went wrong",
        variant: "destructive",
      });
    }
  };

  const openEditDialog = (u: User) => {
    setSelectedUser(u);
    editForm.reset({
      username: u.username,
      password: "",
      isAdmin: u.isAdmin,
    });
    setEditDialogOpen(true);
  };

  const openDeleteDialog = (u: User) => {
    setSelectedUser(u);
    setDeleteDialogOpen(true);
  };

  if (usersLoading) {
    return (
      <div className="h-full overflow-auto">
        <div className="container max-w-7xl mx-auto p-6 space-y-6">
          <div className="space-y-1">
            <Skeleton className="h-9 w-64" />
            <Skeleton className="h-5 w-48" />
          </div>
          <Skeleton className="h-10 w-full" />
          <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
            {[1, 2, 3].map((i) => (
              <Card key={i}>
                <CardHeader>
                  <Skeleton className="h-6 w-3/4" />
                  <Skeleton className="h-4 w-1/2" />
                </CardHeader>
                <CardContent>
                  <Skeleton className="h-10 w-full" />
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full overflow-auto">
      <div className="container max-w-7xl mx-auto p-6 space-y-6">
        {/* Header */}
        <div className="flex flex-col gap-4 sm:flex-row sm:items-center sm:justify-between">
          <div className="space-y-1">
            <h1 className="text-3xl font-semibold tracking-tight">
              User Management
            </h1>
            <p className="text-muted-foreground">
              Manage users and permissions
            </p>
          </div>
          <Button
            onClick={() => setCreateDialogOpen(true)}
            data-testid="button-create-user"
          >
            <UserPlus className="mr-2 h-4 w-4" />
            Create User
          </Button>
        </div>

        {/* Search */}
        <div className="relative">
          <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
          <Input
            placeholder="Search users..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-9"
            data-testid="input-search-users"
          />
        </div>

        {/* Stats */}
        <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
          <Card>
            <CardHeader className="flex flex-row items-center gap-2 space-y-0 pb-2">
              <Users className="h-4 w-4 text-muted-foreground" />
              <CardTitle className="text-sm font-medium">Total Users</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-total-users">
                {usersList.length}
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center gap-2 space-y-0 pb-2">
              <Shield className="h-4 w-4 text-muted-foreground" />
              <CardTitle className="text-sm font-medium">Admins</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-admin-count">
                {usersList.filter((u: User) => u.isAdmin).length}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* User Grid */}
        {filteredUsers.length === 0 ? (
          <Card className="p-12">
            <div className="flex flex-col items-center justify-center text-center space-y-3">
              <Users className="h-12 w-12 text-muted-foreground" />
              <div className="space-y-1">
                <h3 className="font-semibold">No users found</h3>
                <p className="text-sm text-muted-foreground">
                  {searchQuery
                    ? "Try adjusting your search"
                    : "Create your first user to get started"}
                </p>
              </div>
            </div>
          </Card>
        ) : (
          <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
            {filteredUsers.map((u: User) => (
              <Card
                key={u._id}
                className="overflow-hidden"
                data-testid={`card-user-${u._id}`}
              >
                <CardHeader className="gap-2 space-y-0">
                  <div className="flex items-start gap-3">
                    <Avatar className="h-10 w-10">
                      <AvatarFallback>
                        {u.username.slice(0, 2).toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1 min-w-0">
                      <CardTitle
                        className="text-base truncate"
                        data-testid={`text-username-${u._id}`}
                      >
                        {u.username}
                      </CardTitle>
                      {u.isAdmin && (
                        <Badge
                          variant="secondary"
                          className="mt-1"
                          data-testid={`badge-admin-${u._id}`}
                        >
                          <Shield className="mr-1 h-3 w-3" />
                          Admin
                        </Badge>
                      )}
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="flex gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    className="flex-1"
                    onClick={() => openEditDialog(u)}
                    data-testid={`button-edit-${u._id}`}
                  >
                    <Edit className="mr-1 h-3 w-3" />
                    Edit
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    className="flex-1"
                    onClick={() => openDeleteDialog(u)}
                    data-testid={`button-delete-${u._id}`}
                  >
                    <Trash2 className="mr-1 h-3 w-3" />
                    Delete
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Create User Dialog */}
