import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { useToast } from "@/hooks/use-toast";
import {
  MonitorPlay,
  Video,
  Link as LinkIcon,
  Copy,
  LogOut,
  Settings,
} from "lucide-react";
import type { RoomMode } from "@/lib/schema";

interface RoomControlsProps {
  roomId: string;
  roomName: string;
  mode: RoomMode;
  isOwner: boolean;
  onModeChange?: (mode: RoomMode) => void;
  onLeave?: () => void;
}

export function RoomControls({
  roomId,
  roomName,
  mode,
  isOwner,
  onModeChange,
  onLeave,
}: RoomControlsProps) {
  const { toast } = useToast();
  const [selectedMode, setSelectedMode] = useState<RoomMode>(mode);

  const inviteLink = `${window.location.origin}/room/${roomId}`;

  const copyInviteLink = async () => {
    await navigator.clipboard.writeText(inviteLink);
    toast({
      title: "Link copied!",
      description: "Invite link copied to clipboard",
    });
  };

  const handleModeChange = (newMode: RoomMode) => {
    setSelectedMode(newMode);
    onModeChange?.(newMode);
    toast({
      title: "Mode changed",
      description: `Switched to ${newMode === "screenshare" ? "screen share" : "watch party"} mode`,
    });
  };

  return (
    <div className="flex items-center gap-2">
      {isOwner && (
        <Popover>
          <PopoverTrigger asChild>
            <Button
              variant="outline"
              size="sm"
              data-testid="button-room-settings"
            >
              <Settings className="h-4 w-4 sm:mr-1" />
              <span className="hidden sm:inline">Settings</span>
            </Button>
          </PopoverTrigger>
          <PopoverContent className="w-64" data-testid="popover-room-settings" align="end">
            <div className="space-y-4">
              <div className="space-y-2">
                <h4 className="font-medium">Room Mode</h4>
                <RadioGroup
                  value={selectedMode}
                  onValueChange={(value) => handleModeChange(value as RoomMode)}
                >
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem
                      value="watchparty"
                      id="mode-watchparty"
                      data-testid="radio-mode-watchparty"
                    />
                    <Label htmlFor="mode-watchparty" className="flex items-center gap-2 cursor-pointer">
                      <Video className="h-4 w-4" />
                      Watch Party
                    </Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem
                      value="screenshare"
                      id="mode-screenshare"
                      data-testid="radio-mode-screenshare"
                    />
                    <Label htmlFor="mode-screenshare" className="flex items-center gap-2 cursor-pointer">
                      <MonitorPlay className="h-4 w-4" />
                      Screen Share
                    </Label>
                  </div>
                </RadioGroup>
              </div>
            </div>
          </PopoverContent>
        </Popover>
      )}

      <Button
        variant="destructive"
        size="sm"
        onClick={onLeave}
        data-testid="button-leave-room"
      >
        <LogOut className="h-4 w-4 sm:mr-1" />
        <span className="hidden sm:inline">Leave</span>
      </Button>
    </div>
  );
}

src/hooks/useYouTubePlayer.ts.content:
import { useEffect, useRef, useState, useCallback } from 'react';

declare global {
  interface Window {
    YT: any;
    onYouTubeIframeAPIReady: () => void;
  }
}

interface UseYouTubePlayerOptions {
  videoId: string | null;
  isOwner: boolean;
  onStateChange?: (action: 'play' | 'pause' | 'seek', currentTime: number) => void;
}

export function useYouTubePlayer({ videoId, isOwner, onStateChange }: UseYouTubePlayerOptions) {
  const playerRef = useRef<any>(null);
  const playerContainerRef = useRef<HTMLDivElement>(null);
  const [isReady, setIsReady] = useState(false);
  const [isAPIReady, setIsAPIReady] = useState(false);
  const lastActionRef = useRef<{ action: string; time: number } | null>(null);
  const isSyncingRef = useRef(false);

  // Load YouTube IFrame API
  useEffect(() => {
    if (window.YT && window.YT.Player) {
      setIsAPIReady(true);
      return;
    }

    const tag = document.createElement('script');
    tag.src = 'https://www.youtube.com/iframe_api';
    const firstScriptTag = document.getElementsByTagName('script')[0];
    firstScriptTag.parentNode?.insertBefore(tag, firstScriptTag);

    window.onYouTubeIframeAPIReady = () => {
      setIsAPIReady(true);
    };
  }, []);

  // Initialize player when API is ready
  useEffect(() => {
    if (!isAPIReady || !videoId || !playerContainerRef.current) {
      return;
    }

    // Clean up existing player
    if (playerRef.current) {
      playerRef.current.destroy();
      playerRef.current = null;
    }

    // Create new player
    playerRef.current = new window.YT.Player(playerContainerRef.current, {
      videoId,
      playerVars: {
        autoplay: 0,
        controls: isOwner ? 1 : 0,
        disablekb: isOwner ? 0 : 1,
        modestbranding: 1,
        rel: 0,
        fs: 1,
        enablejsapi: 1,
      },
      events: {
        onReady: () => {
          setIsReady(true);
        },
        onStateChange: (event: any) => {
          if (isSyncingRef.current || !isOwner) {
            return;
          }

          const currentTime = playerRef.current?.getCurrentTime() || 0;

          // YT.PlayerState: -1 (unstarted), 0 (ended), 1 (playing), 2 (paused), 3 (buffering), 5 (cued)
          if (event.data === window.YT.PlayerState.PLAYING) {
            const lastAction = lastActionRef.current;
            const timeDiff = lastAction ? Math.abs(currentTime - lastAction.time) : 0;

            // Only send play event if it's a new play or significant time difference
            if (!lastAction || lastAction.action !== 'play' || timeDiff > 1) {
              lastActionRef.current = { action: 'play', time: currentTime };
              onStateChange?.('play', currentTime);
            }
          } else if (event.data === window.YT.PlayerState.PAUSED) {
            const lastAction = lastActionRef.current;

            // Only send pause event if last action wasn't pause
            if (!lastAction || lastAction.action !== 'pause') {
              lastActionRef.current = { action: 'pause', time: currentTime };
              onStateChange?.('pause', currentTime);
            }
          }
        },
      },
    });

    return () => {
      if (playerRef.current) {
        playerRef.current.destroy();
        playerRef.current = null;
      }
    };
  }, [isAPIReady, videoId, isOwner, onStateChange]);

  const syncVideo = useCallback((action: 'play' | 'pause' | 'seek', currentTime: number) => {
    if (!playerRef.current || !isReady) {
      return;
    }

    isSyncingRef.current = true;

    try {
      const playerCurrentTime = playerRef.current.getCurrentTime();
      const timeDiff = Math.abs(playerCurrentTime - currentTime);

      // Always seek if time difference is significant (more than 2 seconds)
      if (timeDiff > 2) {
        playerRef.current.seekTo(currentTime, true);
      }

      // Apply the action
      if (action === 'play') {
        playerRef.current.playVideo();
      } else if (action === 'pause') {
        playerRef.current.pauseVideo();
      } else if (action === 'seek') {
        playerRef.current.seekTo(currentTime, true);
      }

      lastActionRef.current = { action, time: currentTime };
    } finally {
      // Reset sync flag after a short delay
      setTimeout(() => {
        isSyncingRef.current = false;
      }, 500);
    }
  }, [isReady]);

  const play = useCallback(() => {
    if (!playerRef.current || !isReady || !isOwner) return;
    playerRef.current.playVideo();
  }, [isReady, isOwner]);

  const pause = useCallback(() => {
    if (!playerRef.current || !isReady || !isOwner) return;
    playerRef.current.pauseVideo();
  }, [isReady, isOwner]);

  const seekTo = useCallback((time: number) => {
    if (!playerRef.current || !isReady || !isOwner) return;
    playerRef.current.seekTo(time, true);
    const currentTime = playerRef.current.getCurrentTime();
    onStateChange?.('seek', currentTime);
  }, [isReady, isOwner, onStateChange]);

  return {
    playerContainerRef,
    isReady,
    syncVideo,
    play,
    pause,
    seekTo,
  };
}

src/components/bottom-nav.tsx.content:
import { useState } from "react";
import { MessageCircle, Users, Settings } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetHeader, SheetTitle } from "@/components/ui/sheet";
import { ThemeToggle } from "@/components/theme-toggle";
import { useAuth } from "@/contexts/AuthContext";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { useLocation } from "wouter";
import type { RoomMode } from "@/lib/schema";
import { MonitorPlay, Video, LogOut } from "lucide-react";

export type BottomNavTab = "chat" | "participants" | "settings";

interface BottomNavProps {
  activeTab: BottomNavTab;
  onTabChange: (tab: BottomNavTab) => void;
  participantCount?: number;
  roomName?: string;
  roomMode?: RoomMode;
  isOwner?: boolean;
  onModeChange?: (mode: RoomMode) => void;
  onLeaveRoom?: () => void;
}

export function BottomNav({ 
  activeTab, 
  onTabChange, 
  participantCount = 0,
  roomName,
  roomMode,
  isOwner = false,
  onModeChange,
  onLeaveRoom,
}: BottomNavProps) {
  const [settingsOpen, setSettingsOpen] = useState(false);
  const [selectedMode, setSelectedMode] = useState<RoomMode>(roomMode || "watchparty");
  const { user, logout } = useAuth();
  const [, setLocation] = useLocation();

  const handleTabClick = (tab: BottomNavTab) => {
    if (tab === "settings") {
      setSettingsOpen(true);
    } else {
      onTabChange(tab);
    }
  };

  const handleLogout = async () => {
    await logout();
    setLocation("/login");
  };

  const handleModeChange = (newMode: RoomMode) => {
    setSelectedMode(newMode);
    onModeChange?.(newMode);
  };

  return (
    <>
      {/* Bottom Navigation Bar */}
      <div className="fixed bottom-0 left-0 right-0 z-40 bg-background border-t" data-testid="bottom-nav">
        <div className="flex items-center justify-around h-16">
          {/* Chat Tab */}
          <button
            onClick={() => handleTabClick("chat")}
            className={cn(
              "flex flex-col items-center justify-center flex-1 h-full gap-1 transition-colors",
              activeTab === "chat" 
                ? "text-primary" 
                : "text-muted-foreground hover-elevate"
            )}
            data-testid="tab-chat"
          >
            <MessageCircle className="h-5 w-5" />
            <span className="text-xs font-medium">Chat</span>
          </button>

          {/* Participants Tab */}
          <button
            onClick={() => handleTabClick("participants")}
            className={cn(
              "flex flex-col items-center justify-center flex-1 h-full gap-1 transition-colors",
              activeTab === "participants" 
                ? "text-primary" 
                : "text-muted-foreground hover-elevate"
            )}
            data-testid="tab-participants"
          >
            <Users className="h-5 w-5" />
            <span className="text-xs font-medium">Participants</span>
          </button>

          {/* Settings Tab */}
          <button
            onClick={() => handleTabClick("settings")}
            className="flex flex-col items-center justify-center flex-1 h-full gap-1 text-muted-foreground hover-elevate transition-colors"
            data-testid="tab-settings"
          >
            <Settings className="h-5 w-5" />
            <span className="text-xs font-medium">Settings</span>
          </button>
        </div>
      </div>

      {/* Settings Modal */}
      <Sheet open={settingsOpen} onOpenChange={setSettingsOpen}>
        <SheetContent side="bottom" className="h-auto max-h-[80vh]" data-testid="settings-modal">
          <SheetHeader>
            <SheetTitle>Settings</SheetTitle>
          </SheetHeader>

          <div className="mt-6 space-y-6">
            {/* Room Info Section (only show if roomName is provided) */}
            {roomName && (
              <>
                <div className="space-y-3">
                  <h3 className="font-semibold text-sm text-muted-foreground">Room Information</h3>
                  <div className="flex items-center justify-between p-3 rounded-md bg-muted/50">
                    <div>
                      <p className="font-medium">{roomName}</p>
                      <div className="flex items-center gap-2 mt-1">
                        <Badge variant="secondary" className="text-xs">
                          {roomMode === "screenshare" ? "Screen Share" : "Watch Party"}
                        </Badge>
                        <span className="text-xs text-muted-foreground">
                          {participantCount} {participantCount === 1 ? 'participant' : 'participants'}
                        </span>
                      </div>
                    </div>
                  </div>

                  {/* Room Mode Settings (Owner Only) */}
                  {isOwner && onModeChange && (
                    <div className="space-y-3 p-3 rounded-md border">
                      <h4 className="font-medium text-sm">Room Mode</h4>
                      <RadioGroup
                        value={selectedMode}
                        onValueChange={(value) => handleModeChange(value as RoomMode)}
                      >
                        <div className="flex items-center space-x-2">
                          <RadioGroupItem
                            value="watchparty"
                            id="mobile-mode-watchparty"
                          />
                          <Label htmlFor="mobile-mode-watchparty" className="flex items-center gap-2 cursor-pointer">
                            <Video className="h-4 w-4" />
                            Watch Party
                          </Label>
                        </div>
                        <div className="flex items-center space-x-2">
                          <RadioGroupItem
                            value="screenshare"
                            id="mobile-mode-screenshare"
                          />
                          <Label htmlFor="mobile-mode-screenshare" className="flex items-center gap-2 cursor-pointer">
                            <MonitorPlay className="h-4 w-4" />
                            Screen Share
                          </Label>
                        </div>
                      </RadioGroup>
                    </div>
                  )}

                  {/* Leave Room Button */}
                  {onLeaveRoom && (
                    <Button
                      variant="destructive"
                      className="w-full gap-2"
                      onClick={() => {
                        setSettingsOpen(false);
                        onLeaveRoom();
                      }}
                    >
                      <LogOut className="h-4 w-4" />
                      Leave Room
                    </Button>
                  )}
                </div>

                <Separator />
              </>
            )}

            {/* User Profile Section */}
            {user && (
              <div className="flex items-center gap-3 p-4 rounded-md bg-muted/50">
                <Avatar className="h-12 w-12">
                  <AvatarFallback className="text-lg">
                    {user.username.slice(0, 2).toUpperCase()}
                  </AvatarFallback>
                </Avatar>
                <div className="flex-1">
                  <p className="font-semibold">{user.username}</p>
                  <p className="text-sm text-muted-foreground">
                    {user.isAdmin ? "Administrator" : "Member"}
                  </p>
                </div>
              </div>
            )}

            <Separator />

            {/* Theme Toggle */}
            <div className="flex items-center justify-between p-4 rounded-md hover-elevate">
              <div className="flex-1">
                <p className="font-medium">Theme</p>
                <p className="text-sm text-muted-foreground">Toggle dark/light mode</p>
              </div>
              <ThemeToggle />
            </div>

            <Separator />

            {/* Logout Button */}
            <div className="space-y-3">
              <Button
                variant="outline"
                className="w-full"
                onClick={() => setLocation("/profile")}
                data-testid="button-profile"
              >
                View Profile
              </Button>

              <Button
                variant="destructive"
                className="w-full"
                onClick={handleLogout}
                data-testid="button-logout"
              >
                Logout
              </Button>
            </div>
          </div>
        </SheetContent>
      </Sheet>
    </>
  );
}

src/hooks/useScreenShare.ts.content:
import { useState, useCallback, useRef, useEffect } from 'react';

export function useScreenShare() {
  const [isSharing, setIsSharing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const streamRef = useRef<MediaStream | null>(null);
  const videoRef = useRef<HTMLVideoElement | null>(null);

  const startScreenShare = useCallback(async () => {
    try {
      setError(null);

      // Request screen sharing with the browser's native picker
      const stream = await navigator.mediaDevices.getDisplayMedia({
        video: true,
        audio: false,
      });

      streamRef.current = stream;
      setIsSharing(true);

      // Handle when user stops sharing via browser UI
      stream.getVideoTracks()[0].addEventListener('ended', () => {
        stopScreenShare();
      });

      // Attach stream to video element if ref is set
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }

      return stream;
    } catch (err: any) {
      const errorMessage = err.name === 'NotAllowedError' 
        ? 'Screen sharing permission denied'
        : 'Failed to start screen sharing';
      setError(errorMessage);
      setIsSharing(false);
      return null;
    }
  }, []);

  const stopScreenShare = useCallback(() => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    setIsSharing(false);
  }, []);

  const attachVideoElement = useCallback((element: HTMLVideoElement | null) => {
    videoRef.current = element;
    if (element && streamRef.current) {
      element.srcObject = streamRef.current;
    }
  }, []);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopScreenShare();
    };
  }, [stopScreenShare]);

  return {
    isSharing,
    error,
    startScreenShare,
    stopScreenShare,
    attachVideoElement,
  };
}
